import { TabTrigger } from '@/components/azemmur/components/azemmur/tabs/tabs-trigger';
import { TabsRoot } from '@/components/azemmur/components/azemmur/tabs/tabs-root';
import { TabsList } from '@/components/azemmur/components/azemmur/tabs/tabs-list';
import { TabPanel } from '@/components/azemmur/components/azemmur/tabs/tabs-panel';
import { TabsContent } from '@/components/azemmur/components/azemmur/tabs/tabs-content';

type TabsComponent = typeof TabsRoot & {
  List: typeof import('@/components/azemmur/components/azemmur/tabs/tabs-list').TabsList;
  Trigger: typeof import('@/components/azemmur/components/azemmur/tabs/tabs-trigger').TabTrigger;

  Content: typeof import('@/components/azemmur/components/azemmur/tabs/tabs-content').TabsContent;
  Panel: typeof import('@/components/azemmur/components/azemmur/tabs/tabs-panel').TabPanel;
};

const Tabs = TabsRoot as TabsComponent;

Tabs.Trigger = TabTrigger;
Tabs.List = TabsList;
Tabs.Content = TabsContent;
Tabs.Panel = TabPanel;

export { Tabs };

'use client';

import React, {
  useId,
  useState,
  useLayoutEffect,
  useRef,
  useMemo,
  useCallback,
  ComponentProps,
} from 'react';
import { cn } from '@/lib/utils';
import { TabsContext } from '@/components/azemmur/components/azemmur/tabs/tabs-context';
import { TabsVariantProps } from '@/components/azemmur/components/azemmur/tabs/tabs-variants';

interface TabsRootProps extends ComponentProps<'div'>, TabsVariantProps {
  value?: string;
  defaultValue?: string;
  onValueChange?: (value: string) => void;
  ltr?: boolean;
  activation?: 'auto' | 'manual';
  className?: string;
  children: React.ReactNode;
}

function TabsRoot({
  value,
  defaultValue,
  onValueChange,
  ltr = true,
  activation = 'manual',
  className,
  intent,
  styling,
  visuals,
  size,
  shape,
  children,
  ...props
}: TabsRootProps) {
  const tabsId = useId();
  const [internalValue, setInternalValue] = useState(defaultValue ?? '');

  const activeValue = value ?? internalValue;

  const isControlled = value !== undefined;

  const triggerRefs = useRef<Map<string, HTMLButtonElement>>(new Map());

  const registerTrigger = useCallback(
    (val: string, node: HTMLButtonElement | null) => {
      if (node) {
        const prevSize = triggerRefs.current.size;
        triggerRefs.current.set(val, node);
        if (!isControlled && !activeValue && prevSize === 0) {
          setInternalValue(val);
        }
      } else {
        triggerRefs.current.delete(val);
      }
    },
    [isControlled, activeValue],
  );

  const handleValueChange = useCallback(
    (next: string) => {
      if (!isControlled) setInternalValue(next);
      onValueChange?.(next);
    },
    [onValueChange, isControlled],
  );

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent, val: string) => {
      const values = Array.from(triggerRefs.current.keys());
      const index = values.indexOf(val);
      if (index === -1) return;

      const forward = ltr ? 'ArrowRight' : 'ArrowLeft';
      const backward = ltr ? 'ArrowLeft' : 'ArrowRight';

      let next = index;

      if (e.key === forward) next = (index + 1) % values.length;
      else if (e.key === backward)
        next = (index - 1 + values.length) % values.length;
      else if (e.key === 'Home') next = 0;
      else if (e.key === 'End') next = values.length - 1;
      else return;

      e.preventDefault();
      const nextValue = values[next] ?? '';
      triggerRefs.current.get(nextValue)?.focus();
      if (activation === 'auto') handleValueChange(nextValue);
    },
    [ltr, activation, handleValueChange],
  );

  useLayoutEffect(() => {
    if (isControlled) return;
    if (!activeValue && triggerRefs.current.size > 0) {
      const first = triggerRefs.current.keys().next().value;
      if (first) setInternalValue(first);
    }
  }, [activeValue, isControlled]);

  const memoizedValue = useMemo(
    () => ({
      tabsId,
      activeValue,
      onValueChange: handleValueChange,
      registerTrigger,
      handleKeyDown,
      ltr,
      activation,
      getTriggerId: (val: string) => `${tabsId}-trigger-${val}`,
      getPanelId: (val: string) => `${tabsId}-panel-${val}`,
      intent,
      styling,
      visuals,
      size,
      shape,
    }),
    [
      tabsId,
      activeValue,
      handleValueChange,
      registerTrigger,
      handleKeyDown,
      ltr,
      activation,
      intent,
      styling,
      visuals,
      size,
      shape,
    ],
  );

  return (
    <TabsContext.Provider value={memoizedValue}>
      <div className={cn(className)} {...props}>
        {children}
      </div>
    </TabsContext.Provider>
  );
}

export { TabsRoot, type TabsRootProps };

'use client';

import { createContext, useContext } from 'react';
import { TabsVariantProps } from '@/components/azemmur/components/azemmur/tabs/tabs-variants';

interface TabsContextValue extends TabsVariantProps {
  tabsId: string;
  ltr: boolean;
  activeValue?: string;
  onValueChange: (value: string) => void;
  registerTrigger: (value: string, node: HTMLButtonElement | null) => void;
  handleKeyDown: (e: React.KeyboardEvent, value: string) => void;
  getPanelId: (value: string) => string;
  getTriggerId: (value: string) => string;
}

const TabsContext = createContext<TabsContextValue | null>(null);

const useTabs = () => {
  const context = useContext(TabsContext);
  if (!context) {
    throw new Error('Tab components must be used within <Tabs />');
  }
  return context;
};

export { TabsContext, useTabs };

'use client';

import React, { ComponentProps } from 'react';
import { cn } from '@/lib/utils';
import { useTabs } from '@/components/azemmur/components/azemmur/tabs/tabs-context';
import { listVariants } from '@/components/azemmur/components/azemmur/tabs/tabs-variants';

interface TabsListProps extends ComponentProps<'div'> {
  className?: string;
}

function TabsList({ className, children, ...props }: TabsListProps) {
  const { ltr, intent, visuals, size, shape } = useTabs();
  return (
    <div
      role="tablist"
      aria-orientation="horizontal"
      dir={ltr ? 'ltr' : 'rtl'}
      className={cn(listVariants({ intent, visuals, size, shape }), className)}
      {...props}
    >
      {children}
    </div>
  );
}

export { TabsList, type TabsListProps };

import React, {
  useEffect,
  useRef,
  ComponentPropsWithoutRef,
  useImperativeHandle,
  forwardRef,
} from 'react';
import { Button as ButtonPrimitive } from '@/components/azemmur/components/primitives/button';
import { cn } from '@/lib/utils';
import { TabsIndicator as Indicator } from '@/components/azemmur/components/azemmur/tabs/tabs-indicator';
import { useTabs } from '@/components/azemmur/components/azemmur/tabs/tabs-context';
import { triggerVariants } from '@/components/azemmur/components/azemmur/tabs/tabs-variants';

interface TabTriggerProps extends Omit<
  ComponentPropsWithoutRef<typeof ButtonPrimitive>,
  'asChild'
> {
  value: string;
  triggerClassName?: string;
  indicatorClassName?: string;
}

const TabTrigger = forwardRef<HTMLButtonElement, TabTriggerProps>(
  (
    {
      value,
      children,
      className,
      triggerClassName,
      indicatorClassName,
      ...props
    },
    forwardedRef,
  ) => {
    const {
      tabsId,
      activeValue,
      onValueChange,
      registerTrigger,
      handleKeyDown,
      getTriggerId,
      getPanelId,
      intent,
      styling,
      visuals,
      shape,
    } = useTabs();
    const isActive = value === activeValue;
    const internalRef = useRef<HTMLButtonElement>(null);

    useImperativeHandle(forwardedRef, () => internalRef.current!);

    useEffect(() => {
      registerTrigger(value, internalRef.current);
      return () => registerTrigger(value, null);
    }, [value, registerTrigger]);

    return (
      <div
        role="tab"
        aria-selected={isActive}
        aria-controls={getPanelId(value)}
        className={cn('relative inline-block', className)}
      >
        {isActive && (
          <Indicator
            layoutId={`active-tab-${tabsId}`}
            className={indicatorClassName}
            aria-selected={isActive}
            data-state={isActive ? 'active' : 'inactive'}
            intent={intent}
            styling={styling}
            visuals={visuals}
            shape={shape}
          />
        )}

        <ButtonPrimitive
          id={getTriggerId(value)}
          ref={internalRef}
          data-state={isActive ? 'active' : 'inactive'}
          tabIndex={isActive ? 0 : -1}
          onClick={(e) => {
            onValueChange(value);
            props.onClick?.(e);
          }}
          onKeyDown={(e) => {
            handleKeyDown(e, value);
            props.onKeyDown?.(e);
          }}
          className={cn(
            triggerVariants({
              intent,
              visuals,
              styling,
              shape,
            }),
            triggerClassName,
          )}
          {...props}
        >
          {children}
        </ButtonPrimitive>
      </div>
    );
  },
);

TabTrigger.displayName = 'TabTrigger';

export { TabTrigger, type TabTriggerProps };

import type { ComponentProps } from 'react';
import { motion } from 'motion/react';
import { type VariantProps } from 'class-variance-authority';
import { cn } from '@/lib/utils';
import { indicatorVariants } from '@/components/azemmur/components/azemmur/tabs/tabs-variants';

type TabsIndicatorProps = VariantProps<typeof indicatorVariants> &
  ComponentProps<typeof motion.div>;

function TabsIndicator({
  intent,
  styling,
  shape,
  visuals,
  className,
  ...props
}: TabsIndicatorProps) {
  return (
    <motion.div
      aria-hidden="true"
      layout
      {...props}
      className={cn(
        indicatorVariants({
          intent,
          styling,
          shape,
          visuals,
        }),
        className,
      )}
      transition={{ type: 'spring', stiffness: 400, damping: 30, mass: 1 }}
    />
  );
}

export { TabsIndicator, type TabsIndicatorProps };

'use client';

import React, { ComponentProps } from 'react';
import { cn } from '@/lib/utils';
import { useTabs } from '@/components/azemmur/components/azemmur/tabs/tabs-context';

interface TabsContentProps extends ComponentProps<'div'> {
  className?: string;
}

function TabsContent({ className, children, ...props }: TabsContentProps) {
  const { ltr } = useTabs();
  return (
    <div
      dir={ltr ? 'ltr' : 'rtl'}
      className={cn('overflow-hidden', className)}
      {...props}
    >
      {children}
    </div>
  );
}

export { TabsContent, type TabsContentProps };

'use client';

import React, { type ComponentProps } from 'react';
import { motion } from 'motion/react';
import { useTabs } from '@/components/azemmur/components/azemmur/tabs/tabs-context';
import { cn } from '@/lib/utils';

export interface TabPanelProps extends ComponentProps<typeof motion.div> {
  value: string;
}

export function TabPanel({
  value,
  children,
  className,
  ...props
}: TabPanelProps) {
  const { activeValue, getPanelId } = useTabs();
  const isActive = activeValue === value;

  return isActive ? (
    <motion.div
      key={value}
      role="tabpanel"
      id={getPanelId(value)}
      aria-labelledby={getPanelId(value)}
      tabIndex={0}
      initial={{ opacity: 0, y: '100%' }}
      animate={{ opacity: 1, y: '0%' }}
      exit={{ opacity: 0, y: '-100%' }}
      transition={{ duration: 0.2, ease: 'easeOut' }}
      className={cn(
        'mt-4 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring',
        className,
      )}
      {...props}
    >
      {children}
    </motion.div>
  ) : null;
}

import { cva, type VariantProps } from 'class-variance-authority';

const listVariants = cva(
  'relative inline-flex whitespace-nowrap overflow-visible gap-2',
  {
    variants: {
      intent: {
        primary: 'text-primary border-primary',
        secondary: 'text-secondary border-secondary',
        accent: 'text-accent border-accent',
      },
      visuals: {
        filled: '',
        subtle: 'bg-transparent px-8',
        classic: 'bg-transparent border-b px-8',
        outline: 'bg-transparent border-2 overflow-hidden',
        levitate: 'p-2 border-2',
      },
      size: {
        sm: 'text-sm',
        md: 'text-base',
        lg: 'text-lg',
      },
      shape: {
        rounded: 'rounded-md',
        pill: 'rounded-full',
        sharp: 'rounded-none',
      },
    },
    compoundVariants: [
      {
        visuals: 'classic',
        shape: ['rounded', 'pill'],
        className: 'rounded-none',
      },
      {
        intent: ['primary', 'secondary', 'accent'],
        visuals: ['filled', 'levitate'],
        className: 'bg-current/10',
      },
    ],
    defaultVariants: {
      intent: 'primary',
      visuals: 'classic',
      size: 'md',
      shape: 'rounded',
    },
  },
);

const triggerVariants = cva(
  [
    'block px-6 py-2 font-medium transition-colors z-10 relative bg-transparent',
    'focus-visible:outline-none focus-visible:ring-2',
    'after:content-[attr(data-label)] after:block after:h-0 after:invisible',
    '[&[data-state="active"]]:font-bold [&[data-state="active"]]:text-foreground',
  ].join(' '),
  {
    variants: {
      intent: {
        primary: 'text-primary',
        secondary: 'text-secondary',
        accent: 'text-accent',
      },
      visuals: {
        classic: null,
        outline: 'focus-visible:rounded-none',
      },
      styling: {
        underline: null,
        minimal: null,
        solid: null,
      },
      shape: {
        rounded: 'rounded-md',
        pill: 'rounded-full',
        sharp: 'rounded-none',
      },
    },
    compoundVariants: [
      {
        intent: 'primary',
        styling: 'solid',
        className: '[&[data-state="active"]]:text-primary-foreground',
      },
      {
        intent: 'secondary',
        styling: 'solid',
        className: '[&[data-state="active"]]:text-secondary-foreground',
      },
      {
        intent: 'accent',
        styling: 'solid',
        className: '[&[data-state="active"]]:text-accent-foreground',
      },
      {
        visuals: 'classic',
        shape: ['pill', 'rounded'],
        className: 'rounded-b-none rounded-t-md',
      },
    ],
    defaultVariants: {
      intent: 'primary',
      styling: 'underline',
      shape: 'rounded',
    },
  },
);

const indicatorVariants = cva(
  'absolute pointer-events-none transition-colors',
  {
    variants: {
      intent: {
        primary: 'bg-primary',
        secondary: 'bg-secondary',
        accent: 'bg-accent',
      },
      styling: {
        underline: 'bottom-0 h-1 start-0 end-0 bg-primary',
        minimal: 'bottom-0 h-2 w-2 left-1/2 -translate-x-1/2 bg-current',
        solid: 'inset-0',
      },
      visuals: {
        classic: null,
        outline: null,
      },
      shape: {
        rounded: 'rounded-md',
        pill: 'rounded-full',
        sharp: 'rounded-none',
      },
    },
    compoundVariants: [
      {
        styling: 'solid',
        visuals: 'classic',
        shape: ['pill', 'rounded'],
        className: 'rounded-b-none rounded-t-md',
      },
      {
        styling: 'solid',
        visuals: 'outline',
        className: 'rounded-none',
      },
      {
        styling: ['underline', 'minimal'],
        className: '[&[data-state="active"]]:bg-foreground',
      },
    ],
    defaultVariants: {
      styling: 'underline',
      visuals: 'classic',
      shape: 'rounded',
    },
  },
);

type TabsVariantProps = VariantProps<typeof listVariants> &
  VariantProps<typeof triggerVariants>;

export {
  indicatorVariants,
  listVariants,
  triggerVariants,
  type TabsVariantProps,
};
